mapper.select:
select *
from player


        AND ${noticeSearchType} like concat('%',#{noticeSearchText},'%')


order by ${sort}
LIMIT #{pageStart}, #{perPageNum}

mapper.update:
update player name = #{name}, height = #{height}, weight = #{weight}, age = #{age}, position = #{position}, game_match = #{game_match}, playing_time = #{playing_time}, goal = #{goal}, assist = #{assist}, total_point = #{total_point}, yellow_card = #{yellow_card}, red_card = #{red_card}, rating = #{rating}, mvp = #{mvp}, nationality = #{nationality}, play_style = #{play_style}, strength = #{strength}, weakness = #{weakness}, back_number = #{back_number},
file_name = #{file_name},
file_size = #{file_size},
file_bytes = #{file_bytes},
mime_type = #{mime_type}
where player_id = #{player_id}
